export enum JobApplicationStatus {
    // aplicação realizada
    applied = 'applied',

    // aplicação rejeitada
    rejected = 'rejected',

    // aplicação finaliza com contratação
    hired = 'hired',

    // aplicação finaliza sem contratação
    notHired = 'not_hired',

    // candidato desistiu da aplicação
    withdrew = 'withdrew',
}
